DROP TABLE IF EXISTS `M_CHARACTER`;

CREATE TABLE `M_CHARACTER` (
  `id` bigint(10) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `name` varchar(50) NOT NULL COMMENT '名前',
  `attrcd` varchar(10) NOT NULL COMMENT '属性コード',
  `rank` varchar(1) NOT NULL COMMENT '集計ランク',
  `imgfilenm` varchar(100) NOT NULL COMMENT '画像ファイルパス',
  `cmttxt` text NOT NULL COMMENT 'コメントテキスト',
  `delflg` varchar(1) NOT NULL DEFAULT '0' COMMENT '削除フラグ',
  `createdate` varchar(50) DEFAULT NULL COMMENT '作成日時',
  `updatedate` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新日時',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='デフォルトキャラクターマスタ';

